import React, { useRef, useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import googleIcon from "../img/btn_google.png";
import appleIcon from "../img/btn_apple.png";
import slide1 from "../img/hep";
import slide2 from "../img/slide2.jpg";
import slide3 from "../img/slide3.jpg";
import "./Signup.css";

export default function Signup() {
  const navigate = useNavigate();

  // form state
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [agree, setAgree] = useState(false);

  // slideshow state
  const slides = [slide1, slide2, slide3];
  const trackRef = useRef(null);
  const [index, setIndex] = useState(0);

  const goTo = (i) => {
    const track = trackRef.current;
    if (!track) return;
    const clamped = Math.max(0, Math.min(slides.length - 1, i));
    track.scrollTo({ left: clamped * track.clientWidth, behavior: "smooth" });
    setIndex(clamped);
  };

  // tự động cuộn ảnh
  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => {
        const next = (prev + 1) % slides.length;
        goTo(next);
        return next;
      });
    }, 4000);
    return () => clearInterval(interval);
  }, [slides]);

  const handleSignup = (e) => {
    e.preventDefault();
    // xử lý signup ở đây
    console.log({ firstName, lastName, email, password, agree });
    navigate("/");
  };

  return (
    <div className="signup-container">
      {/* bên trái slideshow */}
      <div className="signup-left">
        <button className="btn-back" onClick={() => navigate("/")}>
          Back to website →
        </button>

        <div className="slideshow-wrapper">
          <div className="slideshow-track" ref={trackRef}>
            {slides.map((src, i) => (
              <div className="slide" key={i}>
                <img src={src} alt={`slide-${i}`} />
              </div>
            ))}
          </div>
          <p className="caption">Capturing Moments, Creating Memories</p>
          <div className="dots">
            {slides.map((_, i) => (
              <button
                key={i}
                className={`dot ${i === index ? "active" : ""}`}
                onClick={() => goTo(i)}
                aria-label={`Go to slide ${i + 1}`}
              />
            ))}
          </div>
        </div>
      </div>

      {/* bên phải form */}
      <div className="signup-right">
        <h2>Create an account</h2>
        <p>
          Already have an account? <Link to="/login">Log in</Link>
        </p>
        <form onSubmit={handleSignup} className="signup-form">
          <div className="name-row">
            <input
              type="text"
              placeholder="First name"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Last name"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label className="terms">
            <input
              type="checkbox"
              checked={agree}
              onChange={() => setAgree(!agree)}
              required
            />
            I agree to the <Link to="/terms">Terms & Conditions</Link>
          </label>
          <button type="submit" className="btn-create">
            Create account
          </button>
        </form>
        <div className="divider">
          <span>Or register with</span>
        </div>
        <div className="social-login">
          <button className="btn-google">
            <img src={googleIcon} alt="Google" />
            Google
          </button>
          <button className="btn-apple">
            <img src={appleIcon} alt="Apple" />
            Apple
          </button>
        </div>
      </div>
    </div>
  );
}
