import React, { useRef, useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import googleIcon from "../img/btn_google.png";
import appleIcon from "../img/btn_apple.png";
import dogImg from "../img/dog.png";
import heoImg from "../img/heo.png";
import kongImg from "../img/kong.png";
import "./Signup.css";

export default function Signup() {
  const navigate = useNavigate();
   const [email, setEmail] = useState("");
   const [password, setPassword] = useState("");

   const slides = [dogImg, heoImg, kongImg];
   const trackRef = useRef(null);
   const [index, setIndex] = useState(0);

   // … bên trên code của bạn …

 useEffect(() => {
   const interval = setInterval(() => {
     setIndex((prev) => {
       const next = (prev + 1) % slides.length; // chạy vòng tròn
       goTo(next);
       return next;
     });
   }, 3000); // 3 giây đổi slide

   return () => clearInterval(interval);
 }, [slides]); // thêm slides để khi mảng thay đổi thì reset


   const goTo = (i) => {
     const track = trackRef.current;
     if (!track) return;
     const clamped = Math.max(0, Math.min(slides.length - 1, i));
     track.scrollTo({ left: clamped * track.clientWidth, behavior: "smooth" });
     setIndex(clamped);
   };

   const handleLogin = (e) => {
     e.preventDefault();
     console.log("Email:", email, "Password:", password);
     navigate("/");
   };

   return (
     <div className="login-container">
       {/* Bên trái slideshow */}
       <div className="login-left">
         <button className="btn-back" onClick={() => navigate("/")}>
           ⬅ Back to Home
         </button>

         <div className="slideshow-wrapper">
           <div className="slideshow-track" ref={trackRef}>
             {slides.map((src, i) => (
               <div className="slide" key={i}>
                 <img src={src} alt={`slide-${i}`} />
               </div>
             ))}
           </div>

           <div className="slideshow-controls">
             <button onClick={() => goTo(index - 1)} className="nav-btn">Prev</button>
             <div className="dots">
               {slides.map((_, i) => (
                 <button
                   key={i}
                   className={`dot ${i === index ? "active" : ""}`}
                   onClick={() => goTo(i)}
                   aria-label={`Go to slide ${i + 1}`}
                 />
               ))}
             </div>
             <button onClick={() => goTo(index + 1)} className="nav-btn">Next</button>
           </div>
         </div>
       </div>


      {/* bên phải form */}
      <div className="signup-right">
        <h2>Create an account</h2>
        <p>
          Already have an account? <Link to="/login">Log in</Link>
        </p>
        <form onSubmit={handleSignup} className="signup-form">
          <div className="name-row">
            <input
              type="text"
              placeholder="First name"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Last name"
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
              required
            />
          </div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <label className="terms">
            <input
              type="checkbox"
              checked={agree}
              onChange={() => setAgree(!agree)}
              required
            />
            I agree to the <Link to="/terms">Terms & Conditions</Link>
          </label>
          <button type="submit" className="btn-create">
            Create account
          </button>
        </form>
        <div className="divider">
          <span>Or register with</span>
        </div>
        <div className="social-login">
          <button className="btn-google">
            <img src={googleIcon} alt="Google" />
            Google
          </button>
          <button className="btn-apple">
            <img src={appleIcon} alt="Apple" />
            Apple
          </button>
        </div>
      </div>
    </div>
  );
}
