import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import dunglaiImg from "../img/dunglai.png"; 
import "./ResetPassword.css";

export default function ResetPassword() {
  const navigate = useNavigate();
  const [token, setToken] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleReset = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post(
        "http://localhost:3001/api/auth/reset-password",
        { token, password: newPassword }
      );
      alert(res.data.message || "Đổi mật khẩu thành công!");
      navigate("/login");
    } catch (err) {
      alert(err.response?.data?.message || "Reset thất bại");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reset-wrapper">
      <div className="reset-image">
        <img src={dunglaiImg} alt="Illustration" />
      </div>

      <form className="reset-form" onSubmit={handleReset}>
        <h2 className="reset-title">Đặt lại mật khẩu</h2>
        <p className="reset-subtitle">
          Nhập token nhận được trong email và mật khẩu mới của bạn
        </p>

        <input
          type="text"
          placeholder="Token"
          value={token}
          onChange={(e) => setToken(e.target.value)}
          className="reset-input"
          required
        />

        <input
          type="password"
          placeholder="Mật khẩu mới"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          className="reset-input"
          required
        />

        <button type="submit" className="reset-button" disabled={loading}>
          {loading ? "Đang xác nhận..." : "Xác nhận"}
        </button>

        <button
          type="button"
          className="btn-back"
          onClick={() => navigate("/")}
        >
          ⬅ Quay về Home
        </button>
      </form>
    </div>
  );
}
