import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const [showModal, setShowModal] = useState(true); // tự bật modal khi vào Home

  useEffect(() => {
    setShowModal(true);
  }, []);

  return (
    <div className="h-screen flex items-center justify-center bg-gray-100">
      <h1 className="text-3xl font-bold">Trang Home</h1>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white rounded-2xl shadow-lg p-6 w-[400px] relative text-center">
            {/* nút đóng */}
            <button
              onClick={() => setShowModal(false)}
              className="absolute top-2 right-2 text-gray-500 hover:text-black text-xl"
            >
              ✖
            </button>

            {/* ảnh */}
            <img
              src="/logo192.png"
              alt="Logo"
              className="mx-auto w-20 h-20 mb-3"
            />

            {/* tiêu đề + mô tả */}
            <h2 className="text-2xl font-bold text-red-600 mb-2">Xin chào</h2>
            <p className="text-gray-600 mb-4">
              Vui lòng đăng nhập hoặc đăng ký để tiếp tục.
            </p>

            {/* nút */}
            <div className="flex gap-4 justify-center">
              <Link
                to="/signup"
                className="px-5 py-2 border border-red-500 text-red-500 rounded-lg hover:bg-red-50 transition"
              >
                Đăng ký
              </Link>
              <Link
                to="/login"
                className="px-5 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
              >
                Đăng nhập
              </Link>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
