import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import cauxinImg from "../img/reset_request.png"; // ảnh trong src/img
import "./ResetRequest.css";

export default function ResetRequest() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);

  const handleRequest = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post(
        "http://localhost:3001/api/auth/reset-request",
        { email }
      );
      alert(`Token reset của bạn: ${res.data.token}`);
    } catch (err) {
      alert("Email không tồn tại");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reset-wrapper">
      {/* nút back */}
      <button className="btn-back" onClick={() => navigate("/")}>
        ⬅ Back to Home
      </button>

      <div className="reset-image">
        <img src={cauxinImg} alt="Reset illustration" />
      </div>

      {/* Form bên phải */}
      <form onSubmit={handleRequest} className="reset-form">
        <h2 className="reset-title">Yêu cầu reset mật khẩu</h2>
        <p className="reset-subtitle">
          Nhập email của bạn, hệ thống sẽ gửi token reset về email.
        </p>
        <input
          type="email"
          placeholder="Nhập email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="reset-input"
          required
        />
        <button type="submit" className="reset-button" disabled={loading}>
          {loading ? "Đang gửi..." : "Gửi yêu cầu"}
        </button>
      </form>
    </div>
  );
}
