import React, { useRef, useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import googleIcon from "../img/btn_google.png";
import appleIcon from "../img/btn_apple.png";
import dogImg from "../img/dog.png";
import heoImg from "../img/heo.png";
import kongImg from "../img/kong.png";
import "./Login.css";

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const slides = [dogImg, heoImg, kongImg];
  const trackRef = useRef(null);
  const [index, setIndex] = useState(0);

  // … bên trên code của bạn …

useEffect(() => {
  const interval = setInterval(() => {
    setIndex((prev) => {
      const next = (prev + 1) % slides.length; // chạy vòng tròn
      goTo(next);
      return next;
    });
  }, 3000); // 3 giây đổi slide

  return () => clearInterval(interval);
}, [slides]); // thêm slides để khi mảng thay đổi thì reset


  const goTo = (i) => {
    const track = trackRef.current;
    if (!track) return;
    const clamped = Math.max(0, Math.min(slides.length - 1, i));
    track.scrollTo({ left: clamped * track.clientWidth, behavior: "smooth" });
    setIndex(clamped);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    console.log("Email:", email, "Password:", password);
    navigate("/");
  };

  return (
    <div className="login-container">
      {/* Bên trái slideshow */}
      <div className="login-left">
        <button className="btn-back" onClick={() => navigate("/")}>
          ⬅ Back to Home
        </button>

        <div className="slideshow-wrapper">
          <div className="slideshow-track" ref={trackRef}>
            {slides.map((src, i) => (
              <div className="slide" key={i}>
                <img src={src} alt={`slide-${i}`} />
              </div>
            ))}
          </div>

          <div className="slideshow-controls">
            <button onClick={() => goTo(index - 1)} className="nav-btn">Prev</button>
            <div className="dots">
              {slides.map((_, i) => (
                <button
                  key={i}
                  className={`dot ${i === index ? "active" : ""}`}
                  onClick={() => goTo(i)}
                  aria-label={`Go to slide ${i + 1}`}
                />
              ))}
            </div>
            <button onClick={() => goTo(index + 1)} className="nav-btn">Next</button>
          </div>
        </div>
      </div>

      {/* Bên phải form */}
      <div className="login-right">
        <h2>Sign in to your account</h2>
        <form onSubmit={handleLogin} className="login-form">
          <input
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <input
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit" className="btn-login">Sign in</button>
        </form>

        <div className="extra-links">
          <Link to="/reset-password">Forgot password?</Link>
          <Link to="/reset-req">Forgot password?</Link>
          <p>
            Don’t have an account?{" "}
            <Link to="/signup">Sign up</Link>
          </p>
        </div>

        <div className="divider"><span>or sign in with</span></div>
        <div className="social-login">
          <button className="btn-google">
            <img src={googleIcon} alt="Google" />
            Google
          </button>

          <button className="btn-apple">
            <img src={appleIcon} alt="Apple" />
            Apple
          </button>
        </div>
      </div>
    </div>
  );
}
