import { useState } from "react";
import axios from "axios";

export default function ResetPassword() {
  const [token, setToken] = useState("");
  const [password, setPassword] = useState(""); // ƒë·ªïi th√†nh password cho kh·ªõp backend
  const [loading, setLoading] = useState(false);

  const handleReset = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:3001/api/auth/reset-password", { 
        token, 
        password   // üëà g·ª≠i ƒë√∫ng key backend c·∫ßn
      });
      alert(res.data.message || "ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u th√†nh c√¥ng!");
    } catch (err) {
      alert(err.response?.data?.message || "Reset th·∫•t b·∫°i");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleReset}>
      <h2>Reset Password</h2>

      <input 
        type="text" 
        placeholder="Token" 
        value={token}
        onChange={(e) => setToken(e.target.value)} 
        required
      />

      <input 
        type="password" 
        placeholder="New Password" 
        value={password}
        onChange={(e) => setPassword(e.target.value)} 
        required
      />

      <button type="submit" disabled={loading}>
        {loading ? "ƒêang x·ª≠ l√Ω..." : "Reset"}
      </button>
    </form>
  );
}
