import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./Login.css";

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    console.log("Email:", email, "Password:", password);
    navigate("/");
  };

  return (
    <div className="login-container">
      {/* Bên trái slideshow */}
      <div className="login-left">
        <button className="btn-back" onClick={() => navigate("/")}>
          ⬅ Back to Home
        </button>

        <div className="slideshow">
          <div className="slide fade">
            <img src="/img/slide1.png" alt="slide1" />
          </div>
          <div className="slide fade">
            <img src="/img/slide2.png" alt="slide2" />
          </div>
          <div className="slide fade">
            <img src="/img/slide3.png" alt="slide3" />
          </div>
        </div>
      </div>

      {/* Bên phải form */}
      <div className="login-right">
        <h2>Sign in to your account</h2>
        <form onSubmit={handleLogin} className="login-form">
          <input
            type="email"
            placeholder="Email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <input
            type="password"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit" className="btn-login">Sign in</button>
        </form>

        <div className="extra-links">
          <Link to="/reset-request">Forgot password?</Link>
          <p>
            Don’t have an account?{" "}
            <Link to="/signup">Sign up</Link>
          </p>
        </div>

        <div className="divider"><span>or sign in with</span></div>
        <div className="social-login">
          <button className="btn-google">src="data:image/png;base64," style="height:18px;width:18px" alt="" data-csiid="OdrQaJm5JrXa2roPq8HjwQY_2" data-atf="1"></button>
          <button className="btn-apple">Apple</button>
        </div>
      </div>
    </div>
  );
}
